import UIKit

class zenkindai1ViewController: UIViewController {

    override func viewDidLoad() {
        super.viewDidLoad()
        n = list[doneCount]
        quizCount.text = "第" + String(doneCount+1) + "問"
        quizBox.text = baseQList[n]

    }
    
    //ランダムキー収納変数(数は仮で、10000でなくてもいい)
    var n = 10000
    
    //こなした問題カウント&第n問のn
    var doneCount = 0
    
    
    
    var baseQList = [String]()
    var baseAList = [String]()
    
    
    
    //第n問
    @IBOutlet weak var quizCount: UILabel!
    
    //ランダム化
    @IBAction func randomize(_ sender: Any) {
        list.shuffle()
    }
    
    //次へボタン
    @IBAction func goToNext(_ sender: Any) {
        doneCount += 1
        answerBox.text = "解答"
        if doneCount == baseQList.count {
            doneCount = 0
            n = 10000
            back(_ :(Any).self)
        }
        self.viewDidLoad()
    }
    
    //答え合わせ
    @IBAction func check(_ sender: Any) {
        answerBox.text = baseAList[n]
    }
    
    //問題表示ボックス
    @IBOutlet weak var quizBox: UILabel!
    
    //解答表示ボックス
    @IBOutlet weak var answerBox: UILabel!

    
    
    @IBAction func back(_ sender: Any) {
        self.dismiss(animated: true, completion: nil)
    }
    
